mapper.update:
update video_display_list_detail
set video_display_list_id = #{videoDisplayListId,jdbcType=BIGINT},
  video_list_id = #{videoListId,jdbcType=BIGINT},
  gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},
  gmt_update = #{gmtUpdate,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, video_display_list_id, video_list_id, gmt_create, gmt_update
from video_display_list_detail
where id = #{id,jdbcType=BIGINT}
